document.addEventListener('load', () => {
    debugger;
    [...document.getElementsByTagName("div")].map(el => {
    var match = el.innerText.match(/mdLink="(.+\.md)"/);
    var link = match && match[1];
    if (el.children.length === 0 && link) {
        var root = el.parentNode.parentElement.parentElement;
        root.innerHTML = '<zero-md src="' + link + '"></zero-md>';
        debugger
        root
    }
    return el;
}).filter(Boolean)
})
